const btnClick=document.querySelector(".menu__btn"),menuClose=document.querySelector(".menu"),nav=document.querySelector(".nav");btnClick.addEventListener("click",(()=>{nav.classList.toggle("menu-open")})),menuClose.addEventListener("click",(()=>{nav.classList.remove("menu-open")}));const anchors=document.querySelectorAll('a[href*="#"]');for(let e of anchors)e.addEventListener("click",(function(t){t.preventDefault();const n=e.getAttribute("href").substr(1);document.getElementById(n).scrollIntoView({behavior:"smooth",block:"start"})}));